var pg = require('pg');

var DB = function(host, port, username, password, dbName, debug) {
  this.host     = host;
  this.port     = port;
  this.username = username;
  this.password = password;
  this.dbName   = dbName;
  this.debug    = debug;
};

DB.prototype.connect = function() {
  this.connectString = "postgres://" + this.username + ':' + this.password + '@' + this.host + '/' + this.dbName;
  if (this.debug) {
    console.log('string: ' + this.connectString);
  }
};


module.exports = new DB();

